import React, { Component } from 'react'
import Dropzone from 'react-dropzone';
import './Classifier.css'


export default class Classifier extends Component {
    state={
        files: []
    }
    onDrop = (files) =>{
        this.setState({files})
    }
    render() {
        const files = this.state.files.map(file => (
            <li key={file.name}>
              {file.name} - {file.size} bytes
            </li>
          ));
        return (
            <div>
                <Dropzone onDrop={this.onDrop}  accept='image/png, image/jpeg'>
                    {({isDragActive, getRootProps, getInputProps}) => (
                    <section className="container">
                        <div {...getRootProps({className: 'dropzone back'})}>
                        <input {...getInputProps()} />
                        <i className='far fa-image mb-2 text-muted' style={{fontSize:100}}></i>
                        <p className='text-muted'>{isDragActive ? "Drag some images" :"Drag 'n' drop some files here, or click to select files"}</p>
                        </div>
                        <aside>
                        {files}
                        </aside>
                    </section>
                    )}
                </Dropzone>
            </div>
        )
    }
}
